#include "CTorwart.h"#include <iostream>CTorwart::CTorwart(std::string name, std::string vorname, unsigned int alter, e_position position, unsigned int nummer)    :CSpieler{ name, vorname, alter, position, nummer }{}void CTorwart::setStrafRaumBeherrschung(unsigned int argStrafRaumBeherrschung){    strafRaumBeherrschung = argStrafRaumBeherrschung;}void CTorwart::setLinienQualitaet(unsigned int argLinienQualitaet){    linienQualitaet = argLinienQualitaet;}void CTorwart::setAbwehrKommunikation(unsigned int argAbwehrKommunikation){    abwehrKommunikation = argAbwehrKommunikation;}void CTorwart::print(){    std::cout << name << " " << vorname << " is " << alter << "years old. His position is " << position << ", his number is " << nummer << "." << std::endl;}