#include "CTrainer.h"#include <iostream>CTrainer::CTrainer(std::string name, std::string vorname, unsigned int alter)    : CPerson{ name, vorname, alter}{}void CTrainer::setLizenzStufe(unsigned int argLizenzStufe){    lizenzStufe = argLizenzStufe;}void CTrainer::print(){    std::cout << name << " " << vorname << " " << "is a " << alter << " years old trainer and his lizenz level is " << CTrainer::lizenzStufe << "." << std::endl;}